import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, ActivationEnd } from '@angular/router';
import { OrderService } from 'src/app/services/order.service';
import { Order } from 'src/app/shared/models/Order';

@Component({
  selector: 'app-order-track-page',
  templateUrl: './order-track-page.component.html',
  styleUrls: ['./order-track-page.component.css']
})
export class OrderTrackPageComponent implements OnInit {

  order!:Order;
  constructor(activatedRoute: ActivatedRoute,
              orderService:OrderService) {
     const params = activatedRoute.snapshot.params;
     if(!params.orderId) return;

     orderService.trackOrderById(params.orderId).subscribe(order => {
       this.order = order;
     })

  }

  ngOnInit(): void {
  }

}
<div class="container">
  <div class="content" *ngIf="order">
    <h1>Order #{{order.id}}</h1>
    <div class="details">
      <div><strong>Date</strong> {{order.createdAt | date:'medium'}}</div>
      <div><strong>Name</strong> {{order.name}}</div>
      <div><strong>Address</strong> {{order.address}}</div>
      <div><strong>Status</strong> {{order.status}}</div>
      <div *ngIf="order.paymentId">
        <strong>Payment ID</strong> {{order.paymentId}}
      </div>
    </div>

    <order-items-list [order]="order"></order-items-list>
  </div>

  <div>
    <app-title title="Shipping Address"></app-title>
    <map [order]="order" [readonly]="true"></map>
  </div>
</div>
